import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

const Interactives = ({ messageList, state }) => {
  const [curInteractives, setCurInteractives] = useState([]);

  useEffect(() => {
    console.log(messageList);
    if (
      messageList &&
      messageList.length > 0 &&
      !messageList[messageList.length - 1].isFromMe
    ) {
      setCurInteractives([
        { elementType: 'button', content: 'Option A' },
        { elementType: 'button', content: 'Option B' },
        { elementType: 'button', content: 'Option C' },
      ]);
    } else {
      console.log(state);
      setCurInteractives([]);
    }
  }, [messageList]);

  return (
    <div>
      {curInteractives.map((i, idx) => (
        <button key={idx}>{i.content}</button>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    messageList:
      state.flex.chat.channels[state.flex.session.channelSid].messages,
    state: state,
  };
};

export default connect(mapStateToProps)(Interactives);
