// Package Imports
import React from 'react';
import styled from 'styled-components';

// Material UI
import {
  FormControl,
  Select,
  MenuItem,
  StyledEngineProvider,
} from '@mui/material';

// Consts
const DropdownContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  fieldset: {
    display: none;
  }
`;

const StyledFormControl = styled(FormControl)`
  width: 80%;
  background: #cbe0f6;
  border-radius: 4px;
  border: 0px;
  outline: 0px;
`;

const StyledSelect = styled(Select)`
  padding: 12px;
  color: #233659;
  font-size: 0.9rem;
  border: 0px;
  outline: 0px;
`;

const StyledMenuItem = styled(MenuItem)`
  font-size: 0.8rem;
  border: 0px;
  outline: 0px;
  &:hover {
    background: #1976d2;
    color: #fff;
  }
  border: 0px;
  outline: 0px;
`;

const Dropdown = ({ curInteractives, sendMessage }) => {
  // Render
  return (
    <StyledEngineProvider injectFirst>
      <DropdownContainer>
        <StyledFormControl fullWidth>
          <StyledSelect
            labelId="select-label"
            id="select"
            defaultValue={''}
            displayEmpty={true}
            renderValue={(value) => value || 'Seasonal boxes'}
            onChange={(e) => sendMessage(e.target.value)}
          >
            {curInteractives.options.map((o, idx) => (
              <StyledMenuItem key={o.uuid} value={o.value}>
                {o.content}
              </StyledMenuItem>
            ))}
          </StyledSelect>
        </StyledFormControl>
      </DropdownContainer>
    </StyledEngineProvider>
  );
};

export default Dropdown;
