import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Button } from '@mui/material';

const defaultCurInitatives = { type: '', options: [] };

const Interactives = ({ messageList, state }) => {
  const [curInteractives, setCurInteractives] = useState(defaultCurInitatives);

  useEffect(() => {
    if (
      messageList &&
      messageList.length > 0 &&
      !messageList[messageList.length - 1].isFromMe &&
      messageList[messageList.length - 1].source.state.attributes
        .interactiveWebchatOptions
    ) {
      const lastMessageAttributes =
        messageList[messageList.length - 1].source.state.attributes
          .interactiveWebchatOptions;
      console.log(lastMessageAttributes);
      setCurInteractives(lastMessageAttributes);
    } else {
      setCurInteractives(defaultCurInitatives);
    }
  }, [messageList]);

  useEffect(() => {
    if (curInteractives !== defaultCurInitatives) {
      const el = document.getElementById('interactive-container');
      el.scrollIntoView();
    }
  }, [curInteractives]);

  return (
    <div id="interactive-container">
      {curInteractives.options.map((o, idx) => (
        <Button key={o.uuid} onClick={() => console.log(o.buttonValue)}>
          {o.buttonContent}
        </Button>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    messageList:
      state.flex.chat.channels[state.flex.session.channelSid].messages,
    state: state,
  };
};

export default connect(mapStateToProps)(Interactives);
