import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

const Interactives = ({ messageList }) => {
  const [curInteractives, setCurInteractives] = useState([]);

  useEffect(() => {
    console.log(messageList);
    if (
      messageList &&
      messageList.length > 0 &&
      !messageList[messageList.length - 1].isFromMe
    ) {
      setCurInteractives([
        { elementType: 'button', content: 'Option A' },
        { elementType: 'button', content: 'Option B' },
        { elementType: 'button', content: 'Option C' },
      ]);
    }
  }, [messageList]);

  return (
    <div>
      {curInteractives.forEach((i) => (
        <button>{i.content}</button>
      ))}
    </div>
  );
};

const mapStateToProps = (state) => {
  let channelSid = state.flex.session.channelSid;

  return {
    messageList: state.flex.chat.channels[channelSid].messages,
  };
};

export default connect(mapStateToProps)(Interactives);
