import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import DateAdapter from '@mui/lab/AdapterMoment';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import {
  Button,
  Select,
  FormControl,
  MenuItem,
  TextField,
} from '@mui/material';
import { DatePicker } from '@mui/lab';
import moment from 'moment';

const defaultCurInteractives = { type: '', options: [] };

// type = buttons OR dropdown OR date OR time
// label can be left blank, is used in dropdown only
// options is used for dropdown and buttons and specific props

const Interactives = ({ manager, messageList, channelSid }) => {
  const [curInteractives, setCurInteractives] = useState(
    defaultCurInteractives
  );

  useEffect(() => {
    if (
      messageList &&
      messageList.length > 0 &&
      !messageList[messageList.length - 1].isFromMe &&
      messageList[messageList.length - 1].source.state.attributes
        .interactiveWebchatOptions
    ) {
      const lastMessageAttributes =
        messageList[messageList.length - 1].source.state.attributes
          .interactiveWebchatOptions;
      setCurInteractives(lastMessageAttributes);
    } else {
      setCurInteractives(defaultCurInteractives);
    }
  }, [messageList]);

  useEffect(() => {
    if (curInteractives !== defaultCurInteractives) {
      const el = document.getElementById('interactive-container');
      el.scrollIntoView();
    }
  }, [curInteractives]);

  const sendMessage = async (message) => {
    const curChannel = await manager.chatClient.getChannelBySid(channelSid);
    curChannel.sendMessage(message);
  };

  return (
    <>
      {curInteractives !== defaultCurInteractives ? (
        <div id="interactive-container">
          {curInteractives.type === 'buttons' ? (
            <div className="interactive-buttons-container">
              {curInteractives.options.map((o, idx) => (
                <Button key={o.uuid} onClick={() => sendMessage(o.buttonValue)}>
                  {o.buttonContent}
                </Button>
              ))}
            </div>
          ) : curInteractives.type === 'dropdown' ? (
            <div className="interactive-dropdown-container">
              <FormControl fullWidth>
                <Select
                  labelId="select-label"
                  id="select"
                  defaultValue={curInteractives.options[0].optionValue}
                  onChange={(e) => sendMessage(e.target.value)}
                >
                  {curInteractives.options.map((o, idx) => (
                    <MenuItem key={o.uuid} value={o.optionValue}>
                      {o.optionContent}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </div>
          ) : curInteractives.type === 'calendar' ? (
            <div className="interactive-calendar-container">
              <LocalizationProvider dateAdapter={DateAdapter}>
                <DatePicker
                  renderInput={(props) => <TextField {...props} />}
                  label=""
                  value="hi"
                  defaultValue={moment()}
                  onChange={(value) =>
                    sendMessage(moment(value).format('DD/MM/YYYY'))
                  }
                />
              </LocalizationProvider>
            </div>
          ) : (
            <></>
          )}
        </div>
      ) : (
        <></>
      )}
    </>

    // BUTTONS
    // <div id="interactive-container">
    //   {curInteractives.options.map((o, idx) => (
    //     <Button key={o.uuid} onClick={() => sendMessage(o.buttonValue)}>
    //       {o.buttonContent}
    //     </Button>
    //   ))}
    // </div>

    // DROPDOWN
    // <div id="interactive-container">
    //   {curInteractives.options.length ? (
    //     <FormControl fullWidth>
    //       <InputLabel id="demo-simple-select-label">Age</InputLabel>
    //       <Select
    //         labelId="demo-simple-select-label"
    //         id="demo-simple-select"
    //         value={'10'}
    //         label="Select an option"
    //         onChange={() => console.log('change')}
    //       >
    //         {curInteractives.options.map((o, idx) => (
    //           <MenuItem value={o.buttonValue}>{o.buttonContent}</MenuItem>
    //         ))}
    //       </Select>
    //     </FormControl>
    //   ) : (
    //     <></>
    //   )}
    // </div>
  );
};

const mapStateToProps = (state) => {
  return {
    messageList:
      state.flex.chat.channels[state.flex.session.channelSid].messages,
    channelSid: state.flex.session.channelSid,
  };
};

export default connect(mapStateToProps)(Interactives);

// Studio Flow Examples

