import { Theme as ControlsTheme, CSSProps, CoreThemeColors } from "@twilio/flex-ui-core";
import { MainContainerThemeProps } from "./MainContainer";
import { EntryPointThemeProps } from "./EntryPoint/EntryPoint";
import { MainHeaderThemeProps } from "./MainHeader";
import { PreEngagementCanvasThemeProps } from "./PreEngagementCanvas";
import { PendingEngagementCanvasThemeProps } from "./PendingEngagementCanvas";
import { InvalidPreEngagementCanvasThemeProps } from "./InvalidPreEngagementCanvas/InvalidPreEngagementCanvas";
import { ErrorPageThemeProps } from "./ErrorPage/ErrorPage.definitions";
/**
 * Theme options. extends {@link ControlsTheme}
 * @category Theme
 * @interface Theme
 * @property {CoreThemeColors} colors - Theme colors
 * @property {Theme.MainContainerThemeProps} MainContainer - Theme for main container
 * @property {Theme.EntryPointThemeProps} EntryPoint - Theme for entrypoint component
 * @property {Theme.MainHeaderThemeProps} MainHeader - Theme for main header component
 * @property {Theme.PreEngagementCanvasThemeProps} PreEngagementCanvas - Theme for pre-engagement canvas
 * @property {Theme.PendingEngagementCanvasThemeProps} PendingEngagementCanvas - Theme for pending engagement canvas
 * @property {Theme.InvalidPreEngagementCanvasThemeProps} InvalidPreEngagementCanvas - Theme for invalid engagement canvas
 * @property {Object} Modal - Theme for the modal
 * @property {CSSProps} Modal.Container - Theme for the modal's container
 * @property {CSSProps} Modal.Title - Theme for the modal's title
 * @property {CSSProps} Modal.PrimaryButton - Theme for the modal's primary button
 * @property {CSSProps} Modal.SecondaryButton - Theme for the modal's secondary button
 */
export interface Theme extends ControlsTheme {
    name?: string;
    colors: CoreThemeColors;
    ErrorPage: ErrorPageThemeProps;
    MainContainer: MainContainerThemeProps;
    EntryPoint: EntryPointThemeProps;
    MainHeader: MainHeaderThemeProps;
    PreEngagementCanvas: PreEngagementCanvasThemeProps;
    PendingEngagementCanvas: PendingEngagementCanvasThemeProps;
    InvalidPreEngagementCanvas: InvalidPreEngagementCanvasThemeProps;
    Modal: {
        Container: CSSProps;
        Title: CSSProps;
        PrimaryButton: CSSProps;
        SecondaryButton: CSSProps;
    };
}
