import { Strings as FrameStrings } from "@twilio/flex-ui-core";
/**
 * String map to define localizable language strings
 * @interface Strings
 * @category Templates
 */
export interface Strings<T = string> extends FrameStrings<T> {
    Connecting?: T;
    Disconnected?: T;
    /** EntryPoint component tagline */
    EntryPointTagline?: T;
    /** Refresh button on webchat error page */
    ErrorRefreshButton?: T;
    /** Shown on the pending engagement canvas */
    PendingEngagementMessage?: T;
    /** Shown on the pending engagement canvas, the cancel button */
    PendingEngagementCancelButton?: T;
    /** Shown on the end chat confirmation canvas */
    ConfirmEndEngagementMessage?: T;
    /** Shown on the end chat confirmation canvas */
    ConfirmEndEngagementConfirmButton?: T;
    /** Shown on the end chat confirmation canvas */
    ConfirmEndEngagementDenyButton?: T;
    /** Shown on the bottom left of the PreEngagementCanvas */
    PreEngagementMandatoryFieldMessage?: T;
    /** Shown on the no pre engagement canvas */
    InvalidPreEngagementMessage?: T;
    /** Shown on the no pre engagement canvas, goto docs button */
    InvalidPreEngagementButton?: T;
    /** Author name of the optional predefined messaged displayed in MessagingCanvas */
    PredefinedChatMessageAuthorName?: T;
    /** Body of the optional predefined messaged displayed in MessagingCanvas */
    PredefinedChatMessageBody?: T;
}
