import { CoreThemeColors } from "@twilio/flex-ui-core";
/**
 * Defines colors of a theme
 * @category Theme
 * @memberof ThemeColorsDefinitionCreator
 * @typedef ThemeColorsDefinition
 * @property {CoreThemeColors} colors - object with theme colors
 * @property {boolean} lightTheme - indicates whether UI should consider the theme to be inherently light or dark. Influences chosen text color, hover effects etc.
 */
export interface ThemeColorsDefinition {
    colors: CoreThemeColors;
    lightTheme: boolean;
}
